import { ContractPageComponent } from './contract-page.component';
import { PathConstant } from 'src/app/shared/constant/path.constant';
import { Router } from '@angular/router';
import { Store } from '@ngrx/store';
import { of } from 'rxjs';
import { AppState } from '../../store-ngrx/app.state';
import { DomSanitizer } from '@angular/platform-browser';
import { getContractImageData } from 'src/app/store-ngrx/selectors/e-document.selectors';

describe('ContractPageComponent', () => {
  let component: ContractPageComponent;
  let router: Router = { navigate: jest.fn() } as unknown as Router;
  let store: Store<AppState>;
  let sanitizerMock: DomSanitizer;
  store = {
    select: jest.fn().mockReturnValue(of(null)),
    dispatch: jest.fn()
  } as any

  sanitizerMock = {
    bypassSecurityTrustResourceUrl: jest.fn().mockReturnValue('mockSafeResourceUrl'),
  } as unknown as DomSanitizer;

  beforeEach(async () => {
    component = new ContractPageComponent(sanitizerMock, router, store);
  });

  it('should set contractImage and isLoading toBe false', () => {
    const mockBase64String = ''
    const mockRes = `data:image/png;base64, ${mockBase64String}`;

    jest.spyOn(store, 'select').mockReturnValueOnce(of(mockBase64String))
    const convertBase64Mock = jest.spyOn(component, 'convertBase64').mockReturnValue(mockRes);

    component.getContract();

    expect(store.select).toHaveBeenCalledWith(getContractImageData);
    expect(convertBase64Mock).toHaveBeenCalledWith(mockBase64String);
    expect(component.contractImage).toBe(mockRes);
    expect(component.isLoading).toBe(false);
  });

  it('should call getContract when call ngOnInit', () => {
    jest.spyOn(component, 'getContract');
    component.ngOnInit();
    expect(component.getContract).toBeCalled();
  });

  it('should set variable disabled to be false when call confirmContract', () => {
    component.confirmContract();
    expect(component.disabled).toBe(false);
  });

  it('should call bypassSecurityTrustResourceUrl with the correct base64 string', () => {
    const mockBase64String = ''

    const result = component.convertBase64(mockBase64String);

    expect(sanitizerMock.bypassSecurityTrustResourceUrl).toHaveBeenCalledWith(
      `data:image/png;base64, ${mockBase64String}`
    );
    expect(result).toBe('mockSafeResourceUrl');
  });

  it('should redirect to sign contract page when call onNext', () => {
    const navigateSpy = jest.spyOn(router, 'navigate');
    component.onNext();
    expect(navigateSpy).toHaveBeenCalledWith([PathConstant.SIGN_CONTRACT_PAGE]);
  });

  it('should redirect to capture id card page when call onBack', () => {
    let routerSpy: jest.SpyInstance;
    routerSpy = jest.spyOn(router, 'navigate');
    component.onBack();
    expect(routerSpy).toHaveBeenCalledWith([PathConstant.CAPTURE_ID_CARD]);
  });
});
